mapper.select:
SELECT apply.*,applyrecord.CreateTime as permitStringtime,`user`.`Name` as username,unit.UnitName as unitname,dicitem.Item as statusname FROM apply LEFT JOIN applyrecord ON apply.ApplyId=applyrecord.ApplyId LEFT JOIN `user` on apply.UserId=`user`.UserId LEFT JOIN unit on apply.UnitId=unit.UnitId LEFT JOIN dicitem on apply.`status` =dicitem.dicitemid WHERE
apply.ApplyTime >=#{starttime} and apply.ApplyTime <=#{finishtime}
applyrecord.CreateTime >=#{permittimestart} and applyrecord.CreateTime <=#{permitfinishtime}
and apply.Status=#{status}
and apply.UserId=#{userid}
LIMIT #{offset},#{pageSize}

SELECT COUNT(*) FROM apply LEFT JOIN applyrecord on
apply.ApplyId=applyrecord.ApplyId WHERE

	apply.ApplyTime >=#{starttime} and
	apply.ApplyTime
	<=#{finishtime}


	applyrecord.CreateTime >=#{permittimestart} and
	applyrecord.CreateTime <=#{permitfinishtime}


	and apply.Status=#{status}

mapper.update:
update apply set 
ProcessId = #{processid,jdbcType=VARCHAR}
where apply.ApplyId=#{applyid}
