mapper.select:
SELECT checkdetail.*,notice.CorrentChkDtlId,notice.CreateTime as createtime1,notice.FaultId as faultid1,notice.ProccessId as processid1,notice.`Status` as status1,notice.UnitId,dicitem.Item as statusname from checkdetail LEFT JOIN notice on notice.FaultId=checkdetail.FaultId LEFT JOIN dicitem on dicitem.DicItemId=notice.`Status` WHERE checkdetail.FaultId!=0
and checkdetail.CreateTime >=#{starttime} and checkdetail.CreateTime <=#{finishtime}
and notice.Status=#{status}
and checkdetail.CheckItemId in (SELECT checkitem.CheckItemId FROM checkitem WHERE checkitem.CheckTypeId=#{type})

mapper.update:
update checkdetail
set ProcessId=#{processid,jdbcType=INTEGER}
 where CheckDetailId = #{checkdetailid,jdbcType=INTEGER} and FaultId=#{faultid}
