mapper.select:
select powerline.*,d1.Item as statusname,d2.Item as typename from powerline LEFT JOIN dicitem d1 ON powerline.`Status`=d1.DicItemId LEFT JOIN dicitem d2 ON powerline.Type=d2.DicItemId where
powerline.UnitId=#{unitid}
and powerline.Name like "%"#{name}"%"
LIMIT #{offset},#{pageSize}

select COUNT(*) as count from powerline where

	powerline.UnitId=#{unitid}


	and powerline.Name like "%"#{name}"%"
