mapper.select:
select hangline.*,d1.Item as statusname,d2.Item as typename from hangline LEFT JOIN dicitem d1 ON hangline.`Status`=d1.DicItemId LEFT JOIN dicitem d2 ON hangline.Type=d2.DicItemId
where hangline.Name like "%"#{name}"%"
LIMIT #{offset},#{pageSize}

select COUNT(*) as count from hangline where

	hangline.UnitId=#{unitid}


	and hangline.Name like "%"#{name}"%"
